mapper.select:
SELECT ID, NCS_PRODUCT_NAME, NCS_PRODUCT_PRICE, NCS_PRODUCT_SELLER
FROM TBL_NCS_PRODUCT
WHERE ID = #{id}

mapper.update:
UPDATE TBL_NCS_PRODUCT 
SET 
	NCS_PRODUCT_NAME = #{ncsProductName},
	NCS_PRODUCT_PRICE = #{ncsProductPrice},
	NCS_PRODUCT_SELLER = #{ncsProductSeller}
WHERE ID = #{id}
